    }
    if (guest.hoTen === "" && errorOfHoTen === "") {
      errorOfHoTen = errorOfHoTen + "Tên khách hàng không được bỏ trống!\n";
    }
    var format = /[!@#$%^&*()_+\-=[\]{};':"\\|,.<>?]+/;
    if (format.test(guest.hoTen) && errorOfHoTen === "") {
